mapper.select:
select PostNo, PrjNo, UserNo, PostContent ,PostDate ,PostFile 
  from prjboard
 where PrjNo = #{PrjNo}

select PostNo, PrjNo, UserNo, PostContent ,PostDate ,PostFile 
  from prjboard
 where PrjNo = #{PrjNo} and UserNo = #{userNo.userNo}

mapper.update:
	update prjboard
	   set PrjNo = #{PrjNo}, UserNo = #{userNo.userNo}, PostContent=#{postContent}, PostFile = #{postFile}
     where PostNo = #{postNo}
