mapper.update:
update userinfo
   set Nickname = #{nickName}, Email = #{email}, ZipCode = #{zipCode}, Address = #{address},
    DetailAddress = #{detailAddress}, UserPhone = #{userPhone}, BankName = #{bankName}, BankAccount = #{bankAccount}
 where userId = #{userId}

update userinfo
   set UserPw = password(#{userPw})
 where userId = #{userId}

update userinfo
   set secession = True
 where UserNo = #{userNo}

mapper.select:
select BankName, BankAccount 
from userinfo
	where userNo = #{userNo}
